import React, { Component } from 'react';
import classes from './App.module.css';
import Person from './Components/Persons/Person/Person';

class App extends Component {
  state = {
    persons: [
      { id: '1', name: 'Max', age: 28 },
      { id: '2', name: 'Manu', age: 29 },
      { id: '3', name: 'Stephanie', age: 26 }
    ],
    otherState: 'some other value',
    showPersons: false
  };

  changeNameHandler = (event, id) => {
    // find index by id
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id;
    });
    //get copy of person from state by index
    const person = {
      ...this.state.persons[personIndex]
    };
    //set new value for name
    person.name = event.target.value;
    //get copy of person object fom state
    const persons = [...this.state.persons];
    //get
    persons[personIndex] = person;
    this.setState({ persons: persons });
  };

  toggelShowPesrons = () => {
    const isShow = this.state.showPersons;
    this.setState({
      showPersons: !isShow
    });
  };
  deletePersonHandler(personIndex) {
    const persons = [...this.state.persons];
    persons.splice(personIndex, 1);
    this.setState({ persons: persons });
  }

  render() {
    let personList = null;

    let btnClass = [classes.Button];
    const assigendClasss = [];
    if (this.state.persons.length <= 2) {
      assigendClasss.push(classes.red);
    }
    if (this.state.persons.length <= 1) {
      assigendClasss.push(classes.bold);
    }
    if (this.state.showPersons) {
      personList = (
        <div>
          {this.state.persons.map((p, index) => {
            return (
              <Person
                key={p.id}
                name={p.name}
                age={p.age}
                click={() => this.deletePersonHandler(index)}
                changed={event => this.changeNameHandler(event, p.id)}
              />
            );
          })}
        </div>
      );

      btnClass.push(classes.Red);
    }
    return (
      <div className={classes.App}>
        <h1>Hi, I'm a React App</h1>
        <p className={assigendClasss.join(' ')}>This is really working!</p>
        <button className={btnClass.join(' ')} onClick={this.toggelShowPesrons}>
          toggel show persons
        </button>
        {personList}
      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
  }
}

export default App;
