import React, { Component } from 'react';

class ErorrBoundry extends Component {
  state = {
    hasErorr: false,
    erorrMsg: ''
  };
  componentDidCatch = (erorr, info) => {
    this.setState({ hasErorr: true, erorrMsg: erorr });
  };
  render() {
    if (this.state.hasErorr) {
      return <h1>we got an erorr :{this.state.erorrMsg}</h1>;
    } else {
      return this.props.children;
    }
  }
}

export default ErorrBoundry;
